void extractFile(char *file, int numFile) {

	int i = extractNumCharFile(numFile);
	int y = 0;
	numFile--;

	while(y < i) {
		file[y] = files[numFile];
		numFile += 12;
		y++;
	}

	file[y] = (char)(0x00);

}

